import React, { useState, useEffect } from 'react';
import './App.css';
import MyNavbar from './components/MyNavbar';
import QuizButton from './components/QuizButton';
import MyCard from './components/MyCard';

function App() {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);

  // חיבור ל-API וטעינת השאלות
  useEffect(() => {
    fetch('http://localhost:3000/api/generate-question') // כתובת ה-API שלך
      .then((response) => response.json())
      .then((data) => {
        setQuestions(data); // עדכון השאלות
        setLoading(false); // עצירת טעינה
      })
      .catch((error) => {
        console.error('Error fetching questions:', error);
        setLoading(false); // עצירת טעינה גם במקרה של שגיאה
      });
  }, []); // הרץ רק פעם אחת בעת טעינת הקומפוננטה

  if (loading) {
    return <div>טעינה...</div>;
  }

  return (
    <div className="App">
      <MyNavbar />
      <div style={{ padding: '20px' }}>
        {questions.length > 0 ? (
          <MyCard question={questions[0]} /> // הצגת השאלה הראשונה לדוגמה
        ) : (
          <div>לא נמצאו שאלות</div>
        )}
        <QuizButton />
      </div>
    </div>
  );
}

export default App;
