import React, { useState } from 'react';

const App = () => {
    const [question, setQuestion] = useState('');
    const [answers, setAnswers] = useState([]);
    const [selectedAnswer, setSelectedAnswer] = useState(null);
    const [score, setScore] = useState(0);

    const fetchQuestion = async (topic, difficulty) => {
        const response = await fetch('http://localhost:3000/generate-question', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ topic, difficulty })
        });
        const data = await response.json();
        if (data.question) {
            setQuestion(data.question);
            setAnswers(data.answers);
        }
    };

    const handleAnswerSelection = (answer) => {
        setSelectedAnswer(answer);
        if (answer === 'correct') {
            setScore(score + 1);
        }
    };

    return (
        <div>
            <h1>AI Quiz Game</h1>
            <button onClick={() => fetchQuestion('history', 'medium')}>Get Question</button>
            {question && <div><h2>{question}</h2></div>}
            {answers.length > 0 && answers.map((answer, index) => (
                <button key={index} onClick={() => handleAnswerSelection(answer)}>
                    {answer}
                </button>
            ))}
            <div>Score: {score}</div>
        </div>
    );
};

export default App;
