import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Card, Button, Row, Col } from 'react-bootstrap';
import { motion } from 'framer-motion'; // ✅ אנימציה

function App() {
  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [correctAnswer, setCorrectAnswer] = useState(null);
  const [score, setScore] = useState(0); // ✅ ניקוד
  const [loading, setLoading] = useState(false);
  const [category, setCategory] = useState("");
  const [difficulty, setDifficulty] = useState("");
  const [isQuizStarted, setIsQuizStarted] = useState(false); // ✅ להתחיל רק אחרי בחירת קטגוריה ורמת קושי

  // -> משתנה מיפוי לרמות קושי וניקוד
  const difficultyPoints = {
    Easy: 5,
    Medium: 10,
    Hard: 20,
  };

  useEffect(() => {
    if (isQuizStarted) {
      fetchQuestion(); // Fetch the question only after the quiz starts
    }
  }, [isQuizStarted]);

  const fetchQuestion = async () => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:3000/generate-question', {
        message: `Create a ${difficulty} level question about ${category} with four answers (one correct) in JSON format.`,
      });

      console.log("Response from server:", response.data);

      const data = response.data;
      setQuestion(data.question);
      setAnswers(shuffleArray([...data.answers])); // ✅ ערבוב תשובות
      setCorrectAnswer(data.correct);
      setSelectedAnswer(null);
    } catch (error) {
      console.error('Error fetching question:', error);
    }
    setLoading(false);
  };

  const shuffleArray = (array) => array.sort(() => Math.random() - 0.5);

  const handleAnswerClick = (answer) => {
    if (selectedAnswer) return; // למנוע לחיצה נוספת

    setSelectedAnswer(answer);
    if (answer === correctAnswer) {
      // -> הוספת ניקוד לפי רמת קושי
      const points = difficultyPoints[difficulty] || 0; // ברירת מחדל: 0 נקודות
      setScore((prev) => prev + points);
    }
  };

  const startQuiz = () => {
    if (category && difficulty) {
      setIsQuizStarted(true); // ✅ להתחיל את השאלון
    } else {
      alert("אנא בחר רמת קושי וקטגוריה כדי להתחיל!");
    }
  };

  return (
    <div className="App">
      <Container className="quiz-container text-center">
        {!isQuizStarted ? (
          // מסך בחירה ראשוני
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
            <h3>בחר קטגוריה ורמת קושי כדי להתחיל:</h3>
            <label>
              קטגוריה:
              <select value={category} onChange={(e) => setCategory(e.target.value)}>
                <option value="">-- בחר קטגוריה --</option>
                <option value="General">רקע כללי</option>
                <option value="Science">מדע</option>
                <option value="Math">מתמטיקה</option>
                <option value="History">היסטוריה</option>
                <option value="Sports">ספורט</option>
              </select>
            </label>
            <br />
            <label>
              רמת קושי:
              <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
                <option value="">-- בחר רמת קושי --</option>
                <option value="Easy">קל</option>
                <option value="Medium">בינוני</option>
                <option value="Hard">קשה</option>
              </select>
            </label>
            <br />
            <Button variant="primary" onClick={startQuiz} className="mt-3">
              התחל!
            </Button>
          </motion.div>
        ) : (
          // השאלון עצמו
          <>
            {loading ? (
              <h3>טוען שאלה...</h3>
            ) : (
              <>
                {question && (
                  <motion.div initial={{ opacity: 0, y: -20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }} dir='rtl'>
                    <Card className="question-card">
                      <Card.Body>
                        <Card.Title>{question}</Card.Title>
                      </Card.Body>
                    </Card>
                  </motion.div>
                )}

                <Row className="mt-3">
                  {answers.map((answer, index) => (
                    <Col xs={12} md={6} key={index} className="mb-3">
                      <motion.div whileTap={{ scale: 0.9 }}>
                        <Button
                          variant={
                            selectedAnswer
                              ? answer === correctAnswer
                                ? "success"
                                : answer === selectedAnswer
                                ? "danger"
                                : "outline-secondary"
                              : "primary"
                          }
                          className="answer-button"
                          onClick={() => handleAnswerClick(answer)}
                        >
                          {answer}
                        </Button>
                      </motion.div>
                    </Col>
                  ))}
                </Row>

                {selectedAnswer && (
                  <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
                    <Button variant="info" onClick={fetchQuestion} className="mt-3">
                      המשך לשאלה הבאה
                    </Button>
                  </motion.div>
                )}

                <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }} className="score">
                  <h4>ניקוד: {score}</h4>
                </motion.div>
              </>
            )}
          </>
        )}
      </Container>
    </div>
  );
}

export default App;
