import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import MyNavbar from './components/MyNavbar';
import { Container, Card, Button, Row, Col } from 'react-bootstrap';

function App() {
  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [correctAnswer, setCorrectAnswer] = useState(null);

  useEffect(() => {
    const fetchQuestion = async () => {
      try {
        const response = await axios.post('http://localhost:3000/generate-question', {
          message: 'תן לי שאלה עם ארבע תשובות ואחת מהן נכונה בפורמט JSON',
        });

        console.log("Response from server:", response.data);

        // נניח שהתשובה מתקבלת בפורמט:
        // { question: "מהי בירת צרפת?", answers: ["פריז", "לונדון", "ברלין", "רומא"], correct: "פריז" }
        const data = response.data;
        setQuestion(data.question);
        setAnswers(shuffleArray([...data.answers])); // ערבוב תשובות
        setCorrectAnswer(data.correct);
        setSelectedAnswer(null);
      } catch (error) {
        console.error('Error fetching question:', error);
      }
    };

    fetchQuestion();
  }, []);

  // פונקציה לערבוב תשובות (כדי שלא תמיד התשובה הנכונה תהיה באותו מקום)
  const shuffleArray = (array) => {
    return array.sort(() => Math.random() - 0.5);
  };

  // פונקציה לטיפול בלחיצה על תשובה
  const handleAnswerClick = (answer) => {
    if (selectedAnswer) return; // לאפשר רק לחיצה אחת
    setSelectedAnswer(answer);
  };

  return (
    <div className="App">
      <MyNavbar />
      <Container className="quiz-container text-center">
        {question && (
          <Card className="question-card">
            <Card.Body>
              <Card.Title>{question}</Card.Title>
            </Card.Body>
          </Card>
        )}

        <Row className="mt-3">
          {answers.map((answer, index) => (
            <Col xs={12} md={6} key={index} className="mb-3">
              <Button
                variant={
                  selectedAnswer
                    ? answer === correctAnswer
                      ? "success" // תשובה נכונה -> ירוק
                      : answer === selectedAnswer
                      ? "danger" // תשובה שגויה -> אדום
                      : "outline-secondary"
                    : "primary"
                }
                className="answer-button"
                onClick={() => handleAnswerClick(answer)}
              >
                {answer}
              </Button>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
}

export default App;
