import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, Card, Button, Row, Col } from 'react-bootstrap';
import { motion } from 'framer-motion'; // ✅ אנימציה

function App() {
    const [question, setQuestion] = useState(null);
    const [answers, setAnswers] = useState([]);
    const [selectedAnswer, setSelectedAnswer] = useState(null);
    const [correctAnswer, setCorrectAnswer] = useState(null);
    const [score, setScore] = useState(0);
    const [loading, setLoading] = useState(false);
    const [category, setCategory] = useState("General"); // Default category
    const [difficulty, setDifficulty] = useState("Medium"); // Default difficulty 

    useEffect(() => {
      fetchQuestion(); // Fetch the initial question
    }, [category, difficulty]);

    const fetchQuestion = async () => {
      setLoading(true);
      try {
        const response = await axios.post('http://localhost:3000/generate-question', {
          message: `Create a ${difficulty} level question about ${category} with four answers (one correct) in JSON format.`
        });

        const data = response.data;
        setQuestion(data.question);
        setAnswers(shuffleArray([...data.answers]));
        setCorrectAnswer(data.correct);
        setSelectedAnswer(null);
      } catch (error) {
        console.error('Error fetching question:', error);
      }
      setLoading(false);
    };

    const shuffleArray = (array) => array.sort(() => Math.random() - 0.5);

    const handleAnswerClick = (answer) => {
      if (selectedAnswer) return;

      setSelectedAnswer(answer);
      if (answer === correctAnswer) {
        setScore((prev) => prev + 10);
      }
    };

    return (
      <div className="App">
        <Container className="quiz-container text-center">
          <div className="selection-controls">
            <label>
              Category:
              <select value={category} onChange={(e) => setCategory(e.target.value)}>
                <option value="General">General</option>
                <option value="Science">Science</option>
                <option value="Math">Math</option>
                <option value="History">History</option>
                <option value="Sports">Sports</option>
                {/* Add more options as needed */}
              </select>
            </label>
            <label>
              Difficulty:
              <select value={difficulty} onChange={(e) => setDifficulty(e.target.value)}>
                <option value="Easy">Easy</option>
                <option value="Medium">Medium</option>
                <option value="Hard">Hard</option>
              </select>
            </label>
          </div>

          {loading ? (
            <h3>טוען שאלה...</h3>
          ) : (
            <>
              {question && (
                <motion.div initial={{ opacity: 0, y: -20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }}>
                  <Card className="question-card">
                    <Card.Body>
                      <Card.Title>{question}</Card.Title>
                    </Card.Body>
                  </Card>
                </motion.div>
              )}
              <Row className="mt-3">
                {answers.map((answer, index) => (
                  <Col xs={12} md={6} key={index} className="mb-3">
                    <motion.div whileTap={{ scale: 0.9 }}>
                      <Button
                        variant={
                          selectedAnswer
                            ? answer === correctAnswer
                              ? "success"
                              : answer === selectedAnswer
                              ? "danger"
                              : "outline-secondary"
                            : "primary"
                        }
                        className="answer-button"
                        onClick={() => handleAnswerClick(answer)}
                      >
                        {answer}
                      </Button>
                    </motion.div>
                  </Col>
                ))}
              </Row>

              {selectedAnswer && (
                <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }}>
                  <Button variant="info" onClick={fetchQuestion} className="mt-3">
                    המשך לשאלה הבאה
                  </Button>
                </motion.div>
              )}
            </>
          )}
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }} className="score">
            <h4>ניקוד: {score}</h4>
          </motion.div>
        </Container>
      </div>
    );
  }


export default App;
