import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import MyNavbar from './components/MyNavbar';
import QuizButton from './components/QuizButton';
import MyCard from './components/MyCard';

function App() {
  const [question, setQuestion] = useState(null);
  const [loading, setLoading] = useState(true);

  // חיבור ל-API וטעינת השאלה
  useEffect(() => {
    const fetchQuestion = async () => {
      try {
        const response = await axios.post('http://localhost:3000/generate-question', {
          //message: 'תן לי שאלה לחידון בנושא כללי', // אפשר לשנות את זה בהתאם לצורך שלך
        });
        setQuestion(response.data.question); // עדכון השאלה שהתקבלה מהשרת
        setLoading(false); // עצירת טעינה
      } catch (error) {
        console.error('Error fetching question:', error);
        setLoading(false); // עצירת טעינה גם במקרה של שגיאה
      }
    };

    fetchQuestion(); // קריאה לפונקציה שתשלוף את השאלה
  }, []); // הרץ רק פעם אחת בעת טעינת הקומפוננטה

  if (loading) {
    return <div>טעינה...</div>;
  }

  return (
    <div className="App">
      <MyNavbar />
      <div style={{ padding: '20px' }}>
        {question ? (
          <MyCard question={question} /> // הצגת השאלה שהתקבלה מהשרת
        ) : (
          <div>לא נמצאה שאלה</div>
        )}
        <QuizButton />
      </div>
    </div>
  );
}

export default App;
